def slugify(string):
    """Removes restricted characters from filename and replaces relevant characters with allowed characters."""
    restricted = r'\/:*?"<>|'
    string = string.strip().replace('\"', "'").replace(':', ' -').replace('/', '-')
    while string[-1] in ['.']:
        string = string[0:-1]
    return ''.join(s for s in string if s not in restricted)


