import io
import datetime
from pathlib import Path
from warnings import warn
import textwrap
from .selector import Selector
from .style import Style
from .media import MediaQuery


class StyleSheet:
    def __init__(self, *styles: Style, normalize:bool = True) -> None:
        self.styles = list(styles)
        self.medias = []

        self._by_name = {}
        self._used = set()
        self._media = None

        if normalize:
            self._preamble = open(Path(__file__).parent / "normalize.css").read()
        else:
            self._preamble = None

    def render(self, fp=None, *, dynamic: bool = False):
        if isinstance(fp, (str, Path)):
            fp = open(fp, "wt")

        if fp is None:
            fp = io.StringIO()

        fp.write("/* Made with violetear */\n")
        if dynamic:
            fp.write(f"/* Generating {len(self._used)}/{len(self._by_name)} styles */\n")
        else:
            fp.write(f"/* Generating all {len(self._by_name)} styles */\n")
        fp.write(f"/* Autogenerated on {datetime.datetime.now()} */\n\n")

        fp.write(self._preamble)

        for style in self.styles:
            fp.write(style.css())
            fp.write("\n\n")

        for media in self.medias:
            fp.write(media.css())
            fp.write("{\n")

            for style in media.styles:
                fp.write(textwrap.indent(style.css(), 4 * ' '))
                fp.write("\n\n")

            fp.write("}\n\n")

        if isinstance(fp, io.StringIO):
            result = fp.getvalue()
        else:
            result = None

        fp.close()
        return result

    def add(self, selector: str=None, style:Style=None, *, name:str=None) -> Style:
        if name is None and selector is not None:
            name = selector.replace("#", "_").replace(".", "_").replace("-","_").strip("_")

        if not style:
            style = Style(Selector.from_css(selector))

        if self._media is None:
            self.styles.append(style)
        else:
            self._media.add(style)

        if name is not None:
            self._by_name[name] = style

        return style

    def media(self, min_width: int = None, max_width: int = None) -> MediaQuery:
        media = MediaQuery(self, min_width=min_width, max_width=max_width)
        self.medias.append(media)
        return media

    def redefine(self, style: Style) -> Style:
        style = Style(selector=style.selector)
        self.add(style=style)
        return style

    def __getitem__(self, key) -> Style:
        try:
            style = self._by_name[key]
            self._used.add(style)
            return style
        except KeyError:
            warn(f"Style {key} not defined")
            raise

    def __getattr__(self, key) -> Style:
        return self[key]
